static DNA: &'static str = "";
static DNAMUT: &'static str = "";

pub fn main() {
    DNA.chars()
        .zip(DNAMUT.chars())
        .enumerate()
        .for_each(|(i, (d, m))| {
            if (d != m) {
                println!(
                    "Mutacia nastala na {}  pozicii povodna {d}, nova {m}",
                    i + 1
                )
            }
        })
}
